import { Component, NgModule, OnInit } from '@angular/core';
import { Itens, Noticias, PuxarFeedService } from 'src/app/servico/puxar-feed.service';
import { Platform } from '@ionic/angular';
import { InAppBrowser } from '@awesome-cordova-plugins/in-app-browser/ngx';
import { InfiniteScrollCustomEvent } from '@ionic/angular';

@Component({
  selector: 'app-tecnologia',
  templateUrl: './tecnologia.page.html',
  styleUrls: ['./tecnologia.page.scss'],
})
export class TecnologiaPage implements OnInit {

  noticias_tecnologia : Noticias[] | any


  itens : Itens[] | any


  constructor(private service: PuxarFeedService, private platform: Platform, private links: InAppBrowser) { }

  ngOnInit() {
    this.service.GetAll("https://rss.tecmundo.com.br/feed").subscribe(response =>{
      this.noticias_tecnologia = response;
      this.itens = this.noticias_tecnologia['channel']['item']
      console.log(this.itens)
      console.log(this.itens[0]['enclosure']['@attributes']['url'])

    })
  }

  openLink(url: string) {
    this.links.create(url)
}

}
<ion-header [translucent]="true">
  <ion-toolbar co>
    <ion-title>tecnologia</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">

  <ion-toolbar color="primary" class="titulo_noticia">
    <ion-title style="font-weight: bold; font-size: 18px;">Tecnologia e Pop</ion-title>

  </ion-toolbar>
  <!--Tecnologia-->
  <ion-card *ngFor="let n of itens">

    <ion-label (click)="openLink(n.link)">
      <ion-card-header color="primary">
        <ion-label>{{n.title}}</ion-label>
      </ion-card-header>

      <ion-card-content>
        <ion-img  [src]="n['enclosure']['@attributes']['url']"></ion-img>
        <ion-badge color="danger">{{n.pubDate}}</ion-badge>
        <ion-badge color="primary" style="margin-top: 12px;">Tecmundo</ion-badge>
      </ion-card-content>  
    </ion-label>

  </ion-card>


</ion-content>
