import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ToastController } from '@ionic/angular';
import { PuxarFeedService } from 'src/app/servico/puxar-feed.service';

@Component({
  selector: 'app-cadastro',
  templateUrl: './cadastro.page.html',
  styleUrls: ['./cadastro.page.scss'],
})
export class CadastroPage implements OnInit {
  dados= {
    nome: "",
    email: "",
    telefone: "",
    senha:"",
    foto: FormData
  }

  constructor(private service: PuxarFeedService, private toast: ToastController) { }

  ngOnInit() {
  }

  enviando(form: NgForm){
    console.log(form.value)

    //tratando o formulário
    //verificando se a senha é menor que 8 caracteres
    if(form.value.senha.length < 8){
      return this.toast.create({
        message: 'Senha não pode ter menos de 8 caracteres',
        color: 'danger',
        duration: 3000
      }).then(toast => {
        toast.present()
      })

    }else{
      //Envia as informações de cadaastro pro BD via PHP
    return this.service.create(form.value.nome, form.value.email, form.value.telefone, form.value.senha).subscribe(response => {
      console.log(response)

      //exibe o toast
      if(response == 'EMAIL JA REGISTRADO'){
        this.toast.create({
          message: 'Email já registrado na rede',
          color: 'danger',
          duration: 3000
        }).then(toast => {
          toast.present()
        })
      }else{
        this.toast.create({
          message: 'Usuário Cadastrado com sucesso!',
          color: 'success',
          duration: 3000
        }).then(toast => {
          toast.present()
        })
      }


    })

    }

  }

}
<ion-header [translucent]="true">
  <ion-toolbar color="danger">
    <ion-title>Cadastro</ion-title>
    <ion-menu-button color="dark" slot="start"></ion-menu-button>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <form #f="ngForm" (ngSubmit)="enviando(f)" enctype="multipart/form-data">
    <ion-item>
      <ion-label position="floating">Nome:</ion-label>
      <ion-input [(ngModel)]="dados.nome"  name="nome"></ion-input>
    </ion-item>
    <ion-item>
      <ion-label position="floating">Email:</ion-label>
      <ion-input [(ngModel)]="dados.email" type="email" name="email"></ion-input>
    </ion-item>
    <ion-item>
      <ion-label position="floating">Telefone:</ion-label>
      <ion-input [(ngModel)]="dados.telefone" type="tel" name="telefone"></ion-input>
    </ion-item>
    <ion-item>
      <ion-label position="floating">Senha:</ion-label>
      <ion-input [(ngModel)]="dados.senha" type="password" name="senha"></ion-input>
    </ion-item>

    <ion-item>
      <ion-label>Selecione uma foto:</ion-label><br>
      <ion-input [(ngModel)]="dados.foto" type="file" name="foto"></ion-input>
    </ion-item>



    <ion-button fill="outline" expand="block" type="submit">Cadastrar</ion-button>
  </form>
</ion-content>
