import { Component, NgModule, OnInit } from '@angular/core';
import { Itens, Noticias, PuxarFeedService } from 'src/app/servico/puxar-feed.service';
import { Platform } from '@ionic/angular';
import { InAppBrowser } from '@awesome-cordova-plugins/in-app-browser/ngx';
import { InfiniteScrollCustomEvent } from '@ionic/angular';

@Component({
  selector: 'app-mundo',
  templateUrl: './mundo.page.html',
  styleUrls: ['./mundo.page.scss'],
})
export class MundoPage implements OnInit {

  noticias_mundo : Noticias[] | any


  itens_mundo : Itens[] | any

  constructor(private service: PuxarFeedService, private platform: Platform, private links: InAppBrowser) { }

  ngOnInit() {
    this.service.GetAll("https://www.gazetadopovo.com.br/feed/rss/mundo.xml").subscribe(response =>{
      this.noticias_mundo = response;
      this.itens_mundo = this.noticias_mundo['channel']['item']
      console.log(this.itens_mundo)
      console.log(this.itens_mundo[0]['enclosure']['@attributes']['url'])

    })
  }

  openLink(url: string){
    this.links.create(url)
  }

}
<ion-header [translucent]="true">
  <ion-toolbar color="danger">
    <ion-title>Jornal 24 horas</ion-title>
    <ion-menu-button color="dark" slot="start"></ion-menu-button>

  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-toolbar color="success" class="titulo_noticia">
    <ion-title style="font-weight: bold; font-size: 18px;" >Mundo Internacional</ion-title>
  </ion-toolbar>
  <!--Tecnologia-->
  <ion-card  *ngFor="let n of itens_mundo">

    <ion-label  (click)="openLink(n.link)">
      <ion-card-header color="success">
        <ion-label class="titulo_noticia">{{n.title}}</ion-label>
      </ion-card-header>

      <ion-card-content>
        <ion-img [src]="n['enclosure']['@attributes']['url']"></ion-img>
        <ion-badge color="danger">{{n.pubDate}}</ion-badge>
        <ion-badge color="success" style="margin-top: 12px;">Gazeta</ion-badge>
      </ion-card-content>  

    </ion-label>

  </ion-card>
</ion-content>
