import { Component, NgModule, OnInit } from '@angular/core';
import { Itens, Noticias, PuxarFeedService } from 'src/app/servico/puxar-feed.service';
import { Platform } from '@ionic/angular';
import { InAppBrowser } from '@awesome-cordova-plugins/in-app-browser/ngx';


@Component({
  selector: 'app-home',
  templateUrl: './home.page.html',
  styleUrls: ['./home.page.scss'],
})
export class HomePage implements OnInit {
  noticias_tecnologia : Noticias[] | any
  noticias_economia : Noticias[] | any
  itens : Itens[] | any
  itens_economia : Itens[] | any

  constructor(private service: PuxarFeedService, private platform: Platform, private links: InAppBrowser) { }

  ngOnInit() {
    //links : https://rss.tecmundo.com.br/feed e https://www.gazetadopovo.com.br/feed/rss/economia.xml

    this.service.GetAll("https://rss.tecmundo.com.br/feed").subscribe(response =>{
      this.noticias_tecnologia = response;
      this.itens = this.noticias_tecnologia['channel']['item']
      console.log(this.itens)
      console.log(this.itens[0]['enclosure']['@attributes']['url'])

    })

    this.service.GetAll("https://www.gazetadopovo.com.br/feed/rss/economia.xml").subscribe(response =>{
      this.noticias_economia = response;
      this.itens = this.noticias_economia['channel']['item']
      console.log(this.itens)
      console.log(this.itens[0]['enclosure']['@attributes']['url'])

    })

  }

  openLink(url: string) {
    this.links.create(url)
}

}
<ion-header [translucent]="true">
  <ion-toolbar color="primary">
    <ion-title>Home</ion-title>
    <ion-menu-button slot="start" color="dark"></ion-menu-button>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-toolbar color="primary" class="titulo_noticia">
    <ion-title >Tecnologia</ion-title>
  </ion-toolbar>
  <!--Tecnologia-->
  <ion-card *ngFor="let n of itens">

    <ion-label (click)="openLink(n.link)">
      <ion-card-header color="primary">
        <ion-label class="titulo_noticia">{{n.title}}</ion-label>
      </ion-card-header>

      <ion-card-content>
        <ion-img  [src]="n['enclosure']['@attributes']['url']"></ion-img>
      </ion-card-content>  
    </ion-label>

  </ion-card>

  <ion-toolbar color="warning" class="titulo_noticia">
    <ion-title >Economia</ion-title>
  </ion-toolbar>
  <!--Tecnologia-->
  <ion-card>

    <ion-label >
      <ion-card-header color="primary">
        <ion-label class="titulo_noticia"></ion-label>
      </ion-card-header>

      <ion-card-content>
        <ion-img></ion-img>
      </ion-card-content>  

    </ion-label>

  </ion-card>

</ion-content>
