import { Component, NgModule, OnInit } from '@angular/core';
import { Itens, Noticias, PuxarFeedService } from 'src/app/servico/puxar-feed.service';
import { Platform } from '@ionic/angular';
import { InAppBrowser } from '@awesome-cordova-plugins/in-app-browser/ngx';


@Component({
  selector: 'app-home',
  templateUrl: './home.page.html',
  styleUrls: ['./home.page.scss'],
})
export class HomePage implements OnInit {
  noticias : Noticias[] | any
  itens : Itens[] | any

  constructor(private service: PuxarFeedService, private platform: Platform, private links: InAppBrowser) { }

  ngOnInit() {
    this.service.GetAll().subscribe(response =>{
      this.noticias = response;
      this.itens = this.noticias['channel']['item']
      console.log(this.itens)
      console.log(this.itens[0]['enclosure']['@attributes']['url'])

    })
  }

  openLink(url: string) {
    this.links.create(url)
}}
<ion-header [translucent]="true">
  <ion-toolbar color="primary">
    <ion-title>Home</ion-title>
    <ion-menu-button slot="start" color="dark"></ion-menu-button>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <!--Tecnologia-->
  <ion-button expand="block" class="titulo_noticia">Tecnologia</ion-button>
  <ion-card *ngFor="let n of itens">

    <ion-label (click)="openLink(n.link)">
      <ion-card-header color="primary">
        <ion-label class="titulo_noticia">{{n.title}}</ion-label>
      </ion-card-header>

      <ion-card-content>
        <ion-img  [src]="n['enclosure']['@attributes']['url']"></ion-img>
      </ion-card-content>  
    </ion-label>

  </ion-card>
</ion-content>
